import{d as o,c as d,a,b as s,f as e,o as r,_ as i}from"./index-sOlqRtx3.js";const n="/aprendamaisCourses/assets/foto121-DzYpMVFc.png",p="/aprendamaisCourses/assets/foto122-DM235Ze3.png",l="/aprendamaisCourses/assets/foto123-CmGXWDiT.png",c="/aprendamaisCourses/assets/foto124-By_kDEK1.png",m="/aprendamaisCourses/assets/foto125-D6vz7pBL.png",u="/aprendamaisCourses/assets/foto126-CKnIBnsd.png",v="/aprendamaisCourses/assets/foto127-DaxjlZFr.png",g="/aprendamaisCourses/assets/foto128-C1pUxwKL.png",h="/aprendamaisCourses/assets/foto129-BHSFQGle.png",A="/aprendamaisCourses/assets/foto130-CCCQSwWd.png",b="/aprendamaisCourses/assets/foto131-DiQW7I5u.png",f="data:image/png;base64,",C="data:image/png;base64,",w="data:image/png;base64,",q="data:image/png;base64,",z="data:image/png;base64,",R="data:image/png;base64,",x="data:image/png;base64,",E="data:image/png;base64,",y="data:image/png;base64,",T="data:image/png;base64,",V="data:image/png;base64,",U="data:image/png;base64,",M="data:image/png;base64,",I="/aprendamaisCourses/assets/foto145-ChecrSnO.png",j="/aprendamaisCourses/assets/foto146-NJOjkk3z.png",P="/aprendamaisCourses/assets/foto147-CK_KGFg5.png",O="/aprendamaisCourses/assets/foto148-D_V3UvbP.png",H="/aprendamaisCourses/assets/foto149-C1T8UHZu.png",k="/aprendamaisCourses/assets/foto150-BkbXtPFi.png",B="/aprendamaisCourses/assets/foto151-kMlue9u_.png",Y="/aprendamaisCourses/assets/foto152-x5E86tVq.png",G="/aprendamaisCourses/assets/foto153-BYjUCxVH.png",N="/aprendamaisCourses/assets/foto154-dOQYpNxr.png",S="/aprendamaisCourses/assets/foto155-Dj4PjrcD.png",X={class:"course-content"},Z=o({__name:"Pag01",setup(K){return(F,t)=>(r(),d("main",X,t[0]||(t[0]=[a("p",{class:"paragraph-tex"},"Nem sempre é possível se deslocar de um lugar a outro a pé, por este motivo, muitas vezes é preciso utilizar um meio de transporte - e, às vezes, até mais de um - para chegarmos ao nosso destino. Para isso, é necessário saber como perguntar sobre o meio de transporte correto, horários e valores.",-1),a("p",{class:"paragraph-tex"},"A partir desta situação você irá estudar os nomes de diferentes meios de transporte. Em alguns lugares as linhas de transporte público são identificadas por cores, por este motivo, você as conhecerá aplicadas neste contexto. Também verá como perguntar e informar as horas, já que meios de transporte de passageiros em geral têm horários fixos e verá que a pontualidade é um aspecto importante em países onde a língua inglesa é falada. Além disso, você estudará maneiras de perguntar e informar sobre preços, estruturas que você provavelmente empregará em outras situações além daquelas em que estiver utilizando um transporte em geral.",-1),a("p",{class:"paragraph-tex"},"Por isso, “aperte o cinto de segurança” e prepare-se para um passeio por diferentes means of transportation, colors e times e descubra junto com Rosa “What time is the next bus downtown?”.",-1),a("div",{class:"video-container"},[a("h2",null,"🎥 Assista abaixo ao vídeo introdutório ao módulo do curso."),a("iframe",{class:"video-frame",src:"https://www.youtube.com/embed/2ZE_k83bF1U?si=fu9nDHyVMSyBjd2H",title:"YouTube video player",frameborder:"0",allow:"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share",referrerpolicy:"strict-origin-when-cross-origin",allowfullscreen:""})],-1),a("h2",{class:"main-heading"},"Tópico 2: Meios de Transporte; Família; Rotina e Hábitos – Em Casa",-1),a("h3",{class:"sub-heading"},"1.1 What time is the next bus?",-1),a("p",{class:"paragraph-tex"},"Após algum tempo em Chicago, Rosa já está familiarizada com os arredores de sua casa e da Columbus College of Chicago e consegue chegar a diversos lugares com facilidade andando a pé. Ela decide ir à parte central da cidade e, para isso, precisa utilizar um meio de transporte para se deslocar até lá. Acompanhe no diálogo a seguir o que acontece com Rosa:",-1),a("iframe",{class:"responsive-video",width:"560",height:"315",src:"https://www.youtube.com/embed/k-jbdcSbkrw?si=bxkM_nfaqiIif5gC",title:"YouTube video player",frameborder:"0",allow:"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share",referrerpolicy:"strict-origin-when-cross-origin",allowfullscreen:""},null,-1),s('<div class="dialogue-box" data-v-0225e8ed><p class="dialogue" data-v-0225e8ed><strong data-v-0225e8ed>Rosa:</strong> Excuse me.<br data-v-0225e8ed><strong data-v-0225e8ed>Person:</strong> Yes?<br data-v-0225e8ed><strong data-v-0225e8ed>Rosa:</strong> What’s the bus to Michigan Boulevard?<br data-v-0225e8ed><strong data-v-0225e8ed>Person:</strong> It’s Route 3.<br data-v-0225e8ed><strong data-v-0225e8ed>Rosa:</strong> When is the next bus?<br data-v-0225e8ed><strong data-v-0225e8ed>Person:</strong> At 9:05.<br data-v-0225e8ed><strong data-v-0225e8ed>Rosa:</strong> Oh, at any moment now, then. Thank you. </p></div><p class="paragraph-tex" data-v-0225e8ed>Rosa pega um ônibus para visitar o distrito histórico ao longo da Michigan Boulevard. Veja no conteúdo a seguir como se denominam outros meios de transporte em língua inglesa.</p><p class="paragraph-tex" data-v-0225e8ed>Em grandes cidades como Chicago, é comum haver mais de um meio de transporte público. Veja a seguir o nome de alguns means of transportation.</p><div class="content-img" data-v-0225e8ed><img src="'+n+'" alt="" class="styled-img flags" data-v-0225e8ed><img src="'+p+'" alt="" class="styled-img flags" data-v-0225e8ed><img src="'+l+'" alt="" class="styled-img flags" data-v-0225e8ed><img src="'+c+'" alt="" class="styled-img flags" data-v-0225e8ed><img src="'+m+'" alt="" class="styled-img flags" data-v-0225e8ed><img src="'+u+'" alt="" class="styled-img flags" data-v-0225e8ed><img src="'+v+'" alt="" class="styled-img flags" data-v-0225e8ed><img src="'+g+'" alt="" class="styled-img flags" data-v-0225e8ed><img src="'+h+'" alt="" class="styled-img flags" data-v-0225e8ed><img src="'+A+'" alt="" class="styled-img flags" data-v-0225e8ed><img src="'+b+'" alt="" class="styled-img flags" data-v-0225e8ed><img src="'+f+'" alt="" class="styled-img flags" data-v-0225e8ed></div><h3 class="sub-heading" data-v-0225e8ed>1.2 Question words</h3><p class="paragraph-tex" data-v-0225e8ed>Ao longo das aulas você estudou algumas question words. No diálogo apresentado, Rosa utiliza <strong data-v-0225e8ed>what’s</strong> para perguntar qual é o ônibus que poderia levá-la ao Michigan Boulevard. Veja:</p><ul data-v-0225e8ed><li data-v-0225e8ed><strong data-v-0225e8ed>What’s</strong> the bus to Michigan Boulevard?</li></ul><p class="paragraph-tex" data-v-0225e8ed>Quando você precisar saber qual é o nome ou a rota do meio de transporte, ou ainda o destino para onde você deseja ir, utilize <strong data-v-0225e8ed>what</strong>. Veja os exemplos:</p><ul data-v-0225e8ed><li data-v-0225e8ed><strong data-v-0225e8ed>What’s</strong> the bus to Michigan?</li><li data-v-0225e8ed><strong data-v-0225e8ed>What’s</strong> the subway to Union Station?</li><li data-v-0225e8ed><strong data-v-0225e8ed>What’s</strong> the way to Columbus College?</li></ul><p class="paragraph-tex" data-v-0225e8ed><strong data-v-0225e8ed>When</strong> também foi utilizada por Rosa para perguntar quando o próximo ônibus passaria:</p><ul data-v-0225e8ed><li data-v-0225e8ed><strong data-v-0225e8ed>When</strong> is the next bus?</li></ul><p class="paragraph-tex" data-v-0225e8ed>Essas perguntas geralmente são feitas quando já estamos na bus stop. Mas, e se você quiser saber onde fica o ponto de ônibus? Neste caso, seria utilizado <strong data-v-0225e8ed>where</strong> para perguntar, veja:</p><ul data-v-0225e8ed><li data-v-0225e8ed><strong data-v-0225e8ed>Where’s</strong> the bus stop?</li></ul><p class="paragraph-tex" data-v-0225e8ed>Você provavelmente ouviria a resposta relacionada a algum ponto de referência que serão apresentados no próximo tópico.</p><h3 class="sub-heading" data-v-0225e8ed>1.3 In the Street</h3><p class="paragraph-tex" data-v-0225e8ed>Eventualmente utilizamos pontos de referência para ajudar a encontrar algum lugar, veja a seguir alguns deles:</p><div class="content-table" data-v-0225e8ed><table data-v-0225e8ed><thead data-v-0225e8ed><tr data-v-0225e8ed><th data-v-0225e8ed>Item</th><th data-v-0225e8ed>Português</th></tr></thead><tbody data-v-0225e8ed><tr data-v-0225e8ed><td data-v-0225e8ed>sidewalk (AmE)/ pavement (BrE)</td><td data-v-0225e8ed>calçada</td></tr><tr data-v-0225e8ed><td data-v-0225e8ed>traffic lights</td><td data-v-0225e8ed>semáforo</td></tr><tr data-v-0225e8ed><td data-v-0225e8ed>sign</td><td data-v-0225e8ed>sinal, placa</td></tr><tr data-v-0225e8ed><td data-v-0225e8ed>crossing lane</td><td data-v-0225e8ed>faixa de pedestres</td></tr><tr data-v-0225e8ed><td data-v-0225e8ed>bike lane</td><td data-v-0225e8ed>faixa de ciclistas</td></tr><tr data-v-0225e8ed><td data-v-0225e8ed>roundabout</td><td data-v-0225e8ed>rotatória</td></tr><tr data-v-0225e8ed><td data-v-0225e8ed>crossroads</td><td data-v-0225e8ed>cruzamento</td></tr><tr data-v-0225e8ed><td data-v-0225e8ed>bridge</td><td data-v-0225e8ed>ponte</td></tr></tbody></table></div><p class="paragraph-tex" data-v-0225e8ed>Outra situação que poderá ser vivenciada por alguém que precisa se deslocar utilizando um meio de transporte é perguntar o preço dos tickets.</p><p class="paragraph-tex" data-v-0225e8ed>Esse é o conteúdo que será abordado no próximo tópico.</p><h3 class="sub-heading" data-v-0225e8ed>1.4 How much</h3><p class="paragraph-tex" data-v-0225e8ed>Antes de pegar um meio de transporte pago, normalmente tentamos descobrir o valor da passagem e as formas de pagamento. Para isso, podemos utilizar a mesma pergunta que usamos para saber o preço de qualquer coisa - a estrutura utilizada sempre inicia com <strong data-v-0225e8ed>How much</strong>.</p><p class="paragraph-tex" data-v-0225e8ed>Veja o diálogo entre Rosa e o motorista do ônibus, quando ela pede informação sobre o preço da passagem:</p>',22),a("div",{class:"dialogue-box"},[a("iframe",{class:"responsive-video",width:"560",height:"315",src:"https://www.youtube.com/embed/i2IkLyDzTvs?si=0a8OGgJlBCcGUhN7",title:"YouTube video player",frameborder:"0",allow:"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share",referrerpolicy:"strict-origin-when-cross-origin",allowfullscreen:""}),a("p",{class:"dialogue"},[a("strong",null,"Rosa:"),e(" How much is the ticket?"),a("br"),a("strong",null,"Driver:"),e(" It’s $2.25 in cash."),a("br"),a("strong",null,"Rosa:"),e(" Here you are. ")])],-1),s('<p class="paragraph-tex" data-v-0225e8ed>Acompanhe na tabela outros exemplos do uso de How much no singular (ao referir-se a um ticket) e no plural (ao referir-se a mais de um ticket).</p><div class="content-table" data-v-0225e8ed><table data-v-0225e8ed><thead data-v-0225e8ed><tr data-v-0225e8ed><th data-v-0225e8ed>Singular</th><th data-v-0225e8ed>Plural</th></tr></thead><tbody data-v-0225e8ed><tr data-v-0225e8ed><td data-v-0225e8ed>How much is the subway ticket?<br data-v-0225e8ed>(Quanto é a passagem do metrô?)</td><td data-v-0225e8ed>How much are two Chicago Cards?<br data-v-0225e8ed>(Quanto custam dois Chicago Cards?)</td></tr><tr data-v-0225e8ed><td data-v-0225e8ed>How much is the ticket to New York?<br data-v-0225e8ed>(Quanto é a passagem para New York?)</td><td data-v-0225e8ed>How much are the round-trip tickets?<br data-v-0225e8ed>(Quanto custam as passagens de ida e volta?)</td></tr></tbody></table></div><p class="paragraph-tex" data-v-0225e8ed>Ao responder uma pergunta com How much..? , os números podem ser ditos de diferentes formas. Acompanhe como ficaria para o valor de $2,50:</p><div class="content-table" data-v-0225e8ed><table data-v-0225e8ed><tbody data-v-0225e8ed><tr data-v-0225e8ed><th data-v-0225e8ed>Two fifty</th><th data-v-0225e8ed>Two dollars fifty cents</th><th data-v-0225e8ed>It’s two fifty</th></tr></tbody></table></div><p class="paragraph-tex" data-v-0225e8ed>Ao saber o valor do ticket você poderá efetuar o pagamento. As opções mais comuns são: </p><div class="content-table" data-v-0225e8ed><table data-v-0225e8ed><tbody data-v-0225e8ed><tr data-v-0225e8ed><th data-v-0225e8ed>credit card (cartão de crédito)</th><th data-v-0225e8ed>cash (dinheiro (em espécie))</th></tr><tr data-v-0225e8ed><th data-v-0225e8ed>bills (notas)</th><th data-v-0225e8ed>coins (moedas)</th></tr></tbody></table></div><p class="paragraph-tex" data-v-0225e8ed>No caso de você pagar por algo in cash você pode receber change. Nos países de língua inglesa, as currencies (moedas) utilizadas normalmente são dollar (the USA and Canada) ou pound (libra) (England), e o nome das frações são cents (centavo) e pence (centavo), respectivamente.</p><h3 class="sub-heading" data-v-0225e8ed>1.5 Colors</h3><p class="paragraph-tex" data-v-0225e8ed>Em diversos lugares as linhas de ônibus, trem e metrô são identificados por cores. Veja alguns exemplos:</p><ul data-v-0225e8ed><li data-v-0225e8ed>The bus line which takes Rosa downtown is the <strong data-v-0225e8ed>green</strong> one.</li><li data-v-0225e8ed>The subway line which takes Carlton to work is the <strong data-v-0225e8ed>yellow</strong> one.</li></ul><p class="paragraph-tex" data-v-0225e8ed>Veja a seguir algumas cores utilizadas com maior frequência no cotidiano:</p><div class="content-img" data-v-0225e8ed><img src="'+C+'" alt="" class="styled-img flags" data-v-0225e8ed><img src="'+w+'" alt="" class="styled-img flags" data-v-0225e8ed><img src="'+q+'" alt="" class="styled-img flags" data-v-0225e8ed><img src="'+z+'" alt="" class="styled-img flags" data-v-0225e8ed><img src="'+R+'" alt="" class="styled-img flags" data-v-0225e8ed><img src="'+x+'" alt="" class="styled-img flags" data-v-0225e8ed><img src="'+E+'" alt="" class="styled-img flags" data-v-0225e8ed><img src="'+y+'" alt="" class="styled-img flags" data-v-0225e8ed><img src="'+T+'" alt="" class="styled-img flags" data-v-0225e8ed><img src="'+V+'" alt="" class="styled-img flags" data-v-0225e8ed><img src="'+U+'" alt="" class="styled-img flags" data-v-0225e8ed><img src="'+M+'" alt="" class="styled-img flags" data-v-0225e8ed></div><p class="paragraph-tex" data-v-0225e8ed>As cores podem variar de intensidade e ser caracterizadas como light (clara) ou dark (escuro). Em inglês, quando você quer defini-las assim, basta utilizar estes adjetivos antes da cor.</p><ul data-v-0225e8ed><li data-v-0225e8ed>Rosa’s shoes are <strong data-v-0225e8ed>light</strong> brown. (Os sapatos de Rosa são marrom claro)</li><li data-v-0225e8ed>Rosa’s pants are <strong data-v-0225e8ed>dark</strong> brown. (As calças de Rosa são marrom escuro)</li></ul><p class="paragraph-tex" data-v-0225e8ed>Uma cor, por sua vez, também é um adjetivo, portanto deve vir antes do substantivo. Observe:</p><div class="content-table" data-v-0225e8ed><table data-v-0225e8ed><thead data-v-0225e8ed><tr data-v-0225e8ed><th data-v-0225e8ed>English</th><th data-v-0225e8ed>Português</th></tr></thead><tbody data-v-0225e8ed><tr data-v-0225e8ed><td data-v-0225e8ed>Dube’s black bicycle.</td><td data-v-0225e8ed>A bicicleta preta de Dube.</td></tr><tr data-v-0225e8ed><td data-v-0225e8ed>The dark blue pub.</td><td data-v-0225e8ed>O bar azul-escuro.</td></tr><tr data-v-0225e8ed><td data-v-0225e8ed>Christine’s green bag.</td><td data-v-0225e8ed>A bolsa verde de Christine.</td></tr><tr data-v-0225e8ed><td data-v-0225e8ed>The yellow bus stop sign.</td><td data-v-0225e8ed>A placa amarela da parada de ônibus.</td></tr></tbody></table></div><p class="paragraph-tex" data-v-0225e8ed>Quando se quer saber qual a cor de algo, utilizamos What color combinada com o verbo To be. Acompanhe outros exemplos:</p><div class="content-table" data-v-0225e8ed><table data-v-0225e8ed><thead data-v-0225e8ed><tr data-v-0225e8ed><th colspan="3" data-v-0225e8ed>Singular</th></tr></thead><tbody data-v-0225e8ed><tr data-v-0225e8ed><td rowspan="3" data-v-0225e8ed>What color is</td></tr><tr data-v-0225e8ed><td data-v-0225e8ed>Dube’s bike?</td><td data-v-0225e8ed>Que cor é a bicicleta do Dube?</td></tr><tr data-v-0225e8ed><td data-v-0225e8ed>the subway line to Chinatown?</td><td data-v-0225e8ed>Que cor é a linha de metrô para Chinatown?</td></tr></tbody></table></div><div class="content-table" data-v-0225e8ed><table data-v-0225e8ed><thead data-v-0225e8ed><tr data-v-0225e8ed><th colspan="3" data-v-0225e8ed>Plural</th></tr></thead><tbody data-v-0225e8ed><tr data-v-0225e8ed><td rowspan="3" data-v-0225e8ed>What color are</td></tr><tr data-v-0225e8ed><td data-v-0225e8ed>the train routes to the south?</td><td data-v-0225e8ed>Quais são as cores para as rotas de trem para o sul?</td></tr><tr data-v-0225e8ed><td data-v-0225e8ed>the buses in Chicago?</td><td data-v-0225e8ed>Quais cores são os ônibus em Chicago?</td></tr></tbody></table></div><p class="paragraph-tex" data-v-0225e8ed>É comum que o transporte público tenha um horário de partida e chegada previsto. Por isso, saber a hora exata em que eles vão partir é importante. Como perguntar as horas será o assunto do próximo tópico.</p><h3 class="sub-heading" data-v-0225e8ed>1.6 Asking and telling the time</h3><p class="paragraph-tex" data-v-0225e8ed>Para perguntar sobre a hora em que alguma coisa vai acontecer, utiliza-se a estrutura <strong data-v-0225e8ed>What time is the</strong>, veja o exemplo: </p><ul data-v-0225e8ed><li data-v-0225e8ed><strong data-v-0225e8ed>What time is the</strong> next bus downtown? It’s at 7:50</li></ul><p class="paragraph-tex" data-v-0225e8ed>Mas quando queremos apenas saber as horas, a pergunta é:</p><ul data-v-0225e8ed><li data-v-0225e8ed><strong data-v-0225e8ed>What time is it?</strong> It’s 9:20 PM</li></ul><p class="paragraph-tex" data-v-0225e8ed>Essas perguntas exigem respostas diferentes. Para a primeira, usa-se a preposição <strong data-v-0225e8ed>at</strong>, designando que algo é às &quot;alguma hora&quot;. Para responder a segunda, a oração é iniciada com <strong data-v-0225e8ed>it’s</strong>, expressando que são &quot;determinada hora&quot;. </p><p class="paragraph-tex" data-v-0225e8ed>Para dizer que são 6h47min, dizemos six forty-seven. Quando se trata da hora “em ponto”, ou seja, sem minutos passados ou faltando, acrescentamos a expressão <strong data-v-0225e8ed>o’clock</strong>:</p><ul data-v-0225e8ed><li data-v-0225e8ed>It’s five <strong data-v-0225e8ed>o’clock</strong>. (São cinco horas em ponto)</li></ul><p class="paragraph-tex" data-v-0225e8ed>Em inglês não se costuma utilizar o formato de 24 horas (por exemplo, 17:00 para as cinco horas da tarde) e sim o de 12, acrescentando-se as iniciais “AM” e “PM” conforme o horário seja antes ou após o meio-dia, respectivamente.</p><p class="paragraph-tex" data-v-0225e8ed>Para referir-se ao meio-dia utilizamos noon ou midday e, para meia-noite, midnight. Veja na ilustração de um relógio a seguir como se informa os minutos em inglês:</p><img src="'+I+'" alt="" class="responsive-image" data-v-0225e8ed><p class="paragraph-tex" data-v-0225e8ed>Quando lidamos com as horas em inglês, precisamos atentar para as preposições <strong data-v-0225e8ed>to</strong> e <strong data-v-0225e8ed>past</strong> (ou <strong data-v-0225e8ed>after</strong>). </p><p class="paragraph-tex" data-v-0225e8ed>Quando dizemos que <strong data-v-0225e8ed>passaram</strong> “X” minutos da hora em questão, utilizamos <strong data-v-0225e8ed>past</strong> (ou <strong data-v-0225e8ed>after</strong>). Quando dizemos que <strong data-v-0225e8ed>faltam</strong> “X” minutos <strong data-v-0225e8ed>para</strong> a próxima hora, utilizamos <strong data-v-0225e8ed>to</strong>. </p><p class="paragraph-tex" data-v-0225e8ed>É fácil saber quando é um ou outro, pois assim como em português, os 30 primeiros minutos são contados como passados da hora, enquanto os últimos 30 minutos são contados como restantes para a próxima hora. Veja os exemplos:</p><div class="content-table" data-v-0225e8ed><table data-v-0225e8ed><thead data-v-0225e8ed><tr data-v-0225e8ed><th colspan="2" data-v-0225e8ed>Examples</th></tr></thead><tbody data-v-0225e8ed><tr data-v-0225e8ed><td data-v-0225e8ed>3:20</td><td data-v-0225e8ed>It’s twenty past three</td></tr><tr data-v-0225e8ed><td data-v-0225e8ed>3:30</td><td data-v-0225e8ed>It’s half past three</td></tr><tr data-v-0225e8ed><td data-v-0225e8ed>3:40</td><td data-v-0225e8ed>It’s twenty to four</td></tr><tr data-v-0225e8ed><td data-v-0225e8ed>3:55</td><td data-v-0225e8ed>It’s five to four</td></tr></tbody></table></div><div class="content-img" data-v-0225e8ed><p class="paragraph-tex" data-v-0225e8ed>Se dividirmos uma hora ao meio, teremos duas metades de 30 minutos cada. Assim, para dizer “São dez e meia”, utilizamos a palavra half (metade):</p><img src="'+j+'" alt="" class="styled-img flags" data-v-0225e8ed><p class="paragraph-tex" data-v-0225e8ed>Seguindo o mesmo princípio, se dividirmos meia hora, teremos 15 minutos. Assim, para dizer “São sete e quinze”, ou seja, que passaram quinze minutos (um quarto de hora) das sete utilizamos a fração a quarter (um quarto):</p><img src="'+P+'" alt="" class="styled-img flags" data-v-0225e8ed><p class="paragraph-tex" data-v-0225e8ed>Quando estiverem faltando 15 minutos para uma determinada hora, também utiliza-se a quarter. Por exemplo:</p><img src="'+O+'" alt="" class="styled-img flags" data-v-0225e8ed></div><p class="paragraph-tex" data-v-0225e8ed>Com este conteúdo é possível que você informe ou compreenda informações relacionadas à hora que algo acontece.</p><h3 class="sub-heading" data-v-0225e8ed>1.7 Prepositions of time</h3><p class="paragraph-tex" data-v-0225e8ed>Caso você queira dizer de que horas até que horas algo acontece, ou seja, mencionar o início e o fim do período de tempo, você utilizaria as preposições from e to da seguinte forma:</p><ul data-v-0225e8ed><li data-v-0225e8ed>Carlton works <strong data-v-0225e8ed>from</strong> 7 <strong data-v-0225e8ed>to</strong> 12AM. (Carlton trabalha das 7 ao meio dia.) </li><li data-v-0225e8ed>Rosa’s classes are <strong data-v-0225e8ed>from</strong> Monday <strong data-v-0225e8ed>to</strong> Friday. (As aulas da Rosa são de segunda a sexta-feira.)</li><li data-v-0225e8ed>Winter in Chicago is <strong data-v-0225e8ed>from</strong> December <strong data-v-0225e8ed>to</strong> February. (O inverno em Chicago é de dezembro a fevereiro.)</li></ul><p class="paragraph-tex" data-v-0225e8ed>Se o que você quer saber diz respeito a daqui a quanto tempo algo irá ocorrer a preposição a ser utilizada é <strong data-v-0225e8ed>in</strong>. Veja:</p><ul data-v-0225e8ed><li data-v-0225e8ed>The next train is <strong data-v-0225e8ed>in</strong> 5 minutes. (O próximo trem sai em 5 minutos.)</li></ul><p class="paragraph-tex" data-v-0225e8ed>As preposições <strong data-v-0225e8ed>in</strong>, <strong data-v-0225e8ed>at</strong> e <strong data-v-0225e8ed>on</strong> também são empregadas para indicar lugares. Esse é o conteúdo apresentado a seguir.</p><h3 class="sub-heading" data-v-0225e8ed>1.8 Prepositions of place - means of transportation</h3><p class="paragraph-tex" data-v-0225e8ed>Em se tratando de means of transportation, as preposições de lugar são usadas de uma forma um pouco mais específica. Veja a seguir as preposições adequadas de acordo com cada meio de transporte.</p><img src="'+H+'" alt="" class="responsive-image" data-v-0225e8ed><ul data-v-0225e8ed><li style="text-align:center;" data-v-0225e8ed>She is <strong data-v-0225e8ed>on</strong> the bus.</li></ul><p class="paragraph-tex" data-v-0225e8ed>Para falar que Rosa está no ônibus utiliza-se on porque a ideia é associada ao fato de ser preciso step up to (subir o degrau), atingindo assim um lugar elevado. De modo geral, então, pode-se dizer que com meios de transporte que carregam muitas pessoas - bus, train, subway, cable car, airplane, ship - utiliza-se <strong data-v-0225e8ed>on</strong>. A mesma ideia se aplica a outros meios de transporte em que você também precisa “subir” para poder utilizá-lo. Observe como o <strong data-v-0225e8ed>on</strong> está sendo aplicado nas frases a seguir.</p><div class="content-img" data-v-0225e8ed><img src="'+k+'" alt="" class="styled-img flags" data-v-0225e8ed><img src="'+B+'" alt="" class="styled-img flags" data-v-0225e8ed><img src="'+Y+'" alt="" class="styled-img flags" data-v-0225e8ed></div><p class="paragraph-tex" data-v-0225e8ed>Se você entra em um barco, bote ou carro, a preposição correta é <strong data-v-0225e8ed>in</strong>. Nesta situação tem-se uma ideia de descer ou estar envolvido por um lugar.</p><div class="content-img" data-v-0225e8ed><img src="'+G+'" alt="" class="styled-img flags" data-v-0225e8ed><img src="'+N+'" alt="" class="styled-img flags" data-v-0225e8ed></div><h3 class="sub-heading" data-v-0225e8ed>1.9 Linking Sounds</h3><p data-v-0225e8ed>Quando se está aprendendo uma língua estrangeira, é comum pensarmos que os falantes nativos falam muito rápido, dando a impressão de que transformam duas ou mais palavras em uma só. Isso acontece porque, assim como nós, em português, eles fazem ligações entre os sons das palavras. Quer dizer, “juntam” o som da consoante final de uma palavra com o som da vogal da palavra seguinte. Observe, nas frases a seguir, as ligações realizadas na fala:</p><img src="'+S+'" alt="" class="styled-img flags" style="margin:0 auto;" data-v-0225e8ed><p class="paragraph-tex" data-v-0225e8ed>Veja outros exemplos de linking sounds:</p><div class="content-table" data-v-0225e8ed><table data-v-0225e8ed><tbody data-v-0225e8ed><tr data-v-0225e8ed><th data-v-0225e8ed>Can I have a soda?</th><th data-v-0225e8ed>Get away!</th></tr><tr data-v-0225e8ed><th data-v-0225e8ed>Turn off the lights, please.</th><th data-v-0225e8ed>My dog is lovely.</th></tr></tbody></table></div><p class="paragraph-tex" data-v-0225e8ed>A partir de agora, procure sempre estar atento a essas ligações. Quanto mais você identificá-las, mais fácil será produzi-las oralmente. Desta forma, o speaking se tornará cada vez mais natural.</p><h3 class="sub-heading" data-v-0225e8ed>1.10 Little details</h3><h3 class="sub-heading" data-v-0225e8ed>Punctuality</h3><p class="paragraph-tex" data-v-0225e8ed>In the United States punctuality is very important. Being late is considered rude or impolite. For example, if your class starts at 8AM and you arrive at 8:05, you are late, and it is not good. A very common expression is sharp. People say “The class is at 10 o’clock sharp”, meaning that the class starts exactly at 10 o’clock. Even at social events punctuality is necessary. So, always be on time!</p><h3 class="sub-heading" data-v-0225e8ed>Change</h3><p class="paragraph-tex" data-v-0225e8ed>Another important aspect about the American culture is related to money. You pay for the exact amount of money. For example, if a bag costs $25.07, you pay $25.07. Unless the cashier tells you something like “I’ll make it $25” or “I’ll leave it for $25”, you do not ask for a discount or price lowering. So, make sure you have 1 cent, 5 cents, 10 cents, all sorts of coins with you, they help at these moments.</p><h3 class="sub-heading" data-v-0225e8ed>1.11 Material Complementar - idioms with colors</h3><p class="paragraph-tex" data-v-0225e8ed>Nesta mídia você terá contato com algumas expressões que fazem uso de cores para descrever alguma situação ou sentimento. Em inglês, as cores expressam muito mais do que a cor de algum objeto. Elas também podem sugerir estados de espírito ou situações inusitadas. Conheça alguns desses idioms: </p><ul data-v-0225e8ed><li data-v-0225e8ed><strong data-v-0225e8ed>To tell a white lie:</strong> contar uma “mentirinha”/uma mentira branca</li><li data-v-0225e8ed><strong data-v-0225e8ed>To be blue:</strong> estar deprimido, triste</li><li data-v-0225e8ed><strong data-v-0225e8ed>To be in the red:</strong> estar no vermelho, sem dinheiro</li><li data-v-0225e8ed><strong data-v-0225e8ed>To be green with envy:</strong> ficar roxo de inveja</li><li data-v-0225e8ed><strong data-v-0225e8ed>To paint a black picture of something/somebody:</strong> fazer comentários negativos de algo/alguém</li><li data-v-0225e8ed><strong data-v-0225e8ed>To be in the pink:</strong> estar em boa saúde</li></ul><h3 class="sub-heading" data-v-0225e8ed>1.12 Material Complementar - AM or PM?</h3><p class="paragraph-tex" data-v-0225e8ed>Da meia-noite até as 11h59 você diz <strong data-v-0225e8ed>AM</strong>.</p><p class="paragraph-tex" data-v-0225e8ed>Ou seja, meia-noite é 12<strong data-v-0225e8ed>AM</strong> e três horas da manhã são 3<strong data-v-0225e8ed>AM</strong>.</p><p class="paragraph-tex" data-v-0225e8ed>A partir do meio dia, você diz <strong data-v-0225e8ed>PM</strong>.</p><p class="paragraph-tex" data-v-0225e8ed>Ou seja, meio-dia é 12<strong data-v-0225e8ed>PM</strong> e três horas da tarde são 3<strong data-v-0225e8ed>PM</strong>.</p><p class="paragraph-tex" data-v-0225e8ed>Se por acaso você se confundir, é só pensar em <strong data-v-0225e8ed>AM como antes do meio-dia</strong> e <strong data-v-0225e8ed>PM como pós meio-dia</strong>.</p><h3 class="sub-heading" data-v-0225e8ed>1.13 Material Complementar - on Time</h3><p class="paragraph-tex" data-v-0225e8ed>Se você quer pedir que alguém chegue pontualmente, você pode utilizar as expressões <strong data-v-0225e8ed>on time</strong> (na hora) e <strong data-v-0225e8ed>sharp</strong> (em ponto). </p><p class="paragraph-tex" data-v-0225e8ed>Por exemplo:</p><ul data-v-0225e8ed><li data-v-0225e8ed>Please be here <strong data-v-0225e8ed>on time</strong>. (Por favor esteja aqui na hora.)</li><li data-v-0225e8ed>Please be here at 5 <strong data-v-0225e8ed>sharp</strong>. (Por favor esteja aqui às cinco em ponto.)</li></ul><p class="paragraph-tex" data-v-0225e8ed>Se você quer dizer que chegou alguns minutos antes do horário marcado, você pode usar a expressão <strong data-v-0225e8ed>in time</strong> (em tempo):</p><ul data-v-0225e8ed><li data-v-0225e8ed>Billy arrived <strong data-v-0225e8ed>in time</strong> for the movie. (Billy chegou em tempo para o filme).</li></ul><p class="paragraph-tex" data-v-0225e8ed>Quando queremos dizer que um estabelecimento comercial funciona 24h/dia, 7 dias/semana, a expressão correspondente em inglês é <strong data-v-0225e8ed>24/7</strong>:</p><ul data-v-0225e8ed><li data-v-0225e8ed>The convenience store is open <strong data-v-0225e8ed>24/7</strong>. (A loja de conveniências está aberta vinte e quatro horas por dia, sete dias por semana).</li></ul><p class="paragraph-tex" data-v-0225e8ed>Você também pode usar esta expressão para dizer que trabalha ou estuda muito:</p><ul data-v-0225e8ed><li data-v-0225e8ed>I’m exhausted, I work <strong data-v-0225e8ed>24/7</strong>. (Estou exausto, trabalho sem parar).</li></ul><p class="paragraph-tex" data-v-0225e8ed>Nesse caso, é uma forma de exagerar, não significa que você realmente trabalha ou estuda vinte e quatro horas por dia.</p><p class="paragraph-tex" data-v-0225e8ed>Uma expressão semelhante a 24/7 é <strong data-v-0225e8ed>round the clock</strong>, que designa alguma coisa que acontece dia e noite:</p><ul data-v-0225e8ed><li data-v-0225e8ed>Doctors and nurses work <strong data-v-0225e8ed>round the clock</strong> at the hospital. (Médicos e enfermeiros trabalham dia e noite no hospital.)</li></ul><h3 class="sub-heading" data-v-0225e8ed>1.14 Resumo e Glossário</h3><p class="paragraph-tex" data-v-0225e8ed>Glossary</p><div class="glossary" data-v-0225e8ed><span data-v-0225e8ed>Subway(AmE)/underground,tube (BrE):</span> metrô; <span data-v-0225e8ed>Motorcycle, motorbike:</span> motocicleta; <span data-v-0225e8ed>Bicycle, bike:</span> bicicleta; <span data-v-0225e8ed>Tram, street car:</span> bonde; <span data-v-0225e8ed>Car:</span> carro; <span data-v-0225e8ed>Bus:</span> ônibus; <span data-v-0225e8ed>Taxi:</span> táxi; <span data-v-0225e8ed>Train:</span> trem; <span data-v-0225e8ed>Airplane:</span> avião; <span data-v-0225e8ed>Boat:</span> barco; <span data-v-0225e8ed>Ship:</span> navio; <span data-v-0225e8ed>Ferry boat:</span> balsa; <span data-v-0225e8ed>Credit card:</span> cartão de crédito; <span data-v-0225e8ed>Cash:</span> dinheiro (em espécie); <span data-v-0225e8ed>Bills:</span> notas; <span data-v-0225e8ed>Coins:</span> moedas; <span data-v-0225e8ed>In cash:</span> em dinheiro; <span data-v-0225e8ed>Change:</span> troco; <span data-v-0225e8ed>Currencies:</span> moeda; <span data-v-0225e8ed>Pound:</span> libra; <span data-v-0225e8ed>Impolite:</span> sem educação; <span data-v-0225e8ed>Meaning:</span> querendo dizer; <span data-v-0225e8ed>To start:</span> iniciar; <span data-v-0225e8ed>Even at:</span> mesmo em; <span data-v-0225e8ed>Unless:</span> a menos que; <span data-v-0225e8ed>I’ll make it for…: </span> faço por..; <span data-v-0225e8ed>I’ll leave it for..:</span> deixo por..; <span data-v-0225e8ed>To ask for:</span> pedir; <span data-v-0225e8ed>Price lowering:</span> redução e; <span data-v-0225e8ed>To make sure:</span> certificar-se. </div>',87)])))}}),D=i(Z,[["__scopeId","data-v-0225e8ed"]]);export{D as default};
