import{d as e,c as o,a,b as d,f as t,o as r,_ as n}from"./index-D0b_g50Z.js";const i="/aprendamaisCourses/assets/foto156-CIhVBwVo.png",b="/aprendamaisCourses/assets/foto157-DYnQJEsX.png",l="/aprendamaisCourses/assets/foto158-BMog14Wu.png",v="/aprendamaisCourses/assets/foto159-DHBSDGuC.png",g="/aprendamaisCourses/assets/foto160-B-dvtaZ4.png",p="/aprendamaisCourses/assets/foto161-CGCWOvHJ.png",u="/aprendamaisCourses/assets/foto162-wEzWQSml.png",m="/aprendamaisCourses/assets/foto163-Cwqii6h1.png",h="/aprendamaisCourses/assets/foto164-CJtiG8gE.png",c="/aprendamaisCourses/assets/foto165-ByABaRC5.png",A="data:image/png;base64,",f="/aprendamaisCourses/assets/foto167-BflFHNZF.png",y="data:image/png;base64,",C="data:image/png;base64,",w="/aprendamaisCourses/assets/foto170-tvRfIhqS.png",D="/aprendamaisCourses/assets/foto171-D18RHXpV.png",B="/aprendamaisCourses/assets/foto172-DDroOn_h.png",I="/aprendamaisCourses/assets/foto173-D5Bf7rCv.png",Q="/aprendamaisCourses/assets/foto174-BGNr_kyw.png",E="/aprendamaisCourses/assets/foto175-D2S_RF3t.png",x="/aprendamaisCourses/assets/foto176-B92BFsWX.png",H="/aprendamaisCourses/assets/foto177-DsbOv2BO.png",q="/aprendamaisCourses/assets/foto178-CAhUO---.png",R="/aprendamaisCourses/assets/foto179-Bm4wp9jk.png",P="/aprendamaisCourses/assets/foto180-CbD4zllH.png",Y="/aprendamaisCourses/assets/foto181-CGc6oEtx.png",G="/aprendamaisCourses/assets/foto182-m0JHY8Qx.png",S="data:image/png;base64,",z="data:image/png;base64,",O="data:image/png;base64,",M="/aprendamaisCourses/assets/foto186-6Ikjg9ZT.png",Z="/aprendamaisCourses/assets/foto187-ij0FJpwb.png",F="/aprendamaisCourses/assets/foto188-D1jzHQgU.png",V="/aprendamaisCourses/assets/foto189-CZWIzhIT.png",U="/aprendamaisCourses/assets/foto190-C8Ct0-lK.png",j="/aprendamaisCourses/assets/foto191-C3GWtFyx.png",k={class:"course-content"},W=e({__name:"Pag02",setup(N){return(X,s)=>(r(),o("main",k,s[0]||(s[0]=[a("p",{class:"paragraph-tex"},"Neste módulo você irá acompanhar Rosa e sua turma conhecendo-se melhor e falando sobre suas famílias. Você estudará como perguntar sobre a aparência física de alguém e como responder esse tipo de pergunta. Para isso, você verá não apenas itens de vocabulário sobre características físicas, como também o Possessive case (também conhecido como Genitive case) para designar quem possui determinadas características como, também; o genitivo, usado para indicar posse em geral. Outro assunto a ser estudado é o tempo verbal Present Simple. Ele será utilizado com o verbo To have (ter) para dizer quais características uma pessoa tem.",-1),a("div",{class:"video-container"},[a("h2",null,"🎥 Assista abaixo ao vídeo introdutório ao módulo do curso."),a("iframe",{class:"video-frame",src:"https://www.youtube.com/embed/4Nnmpk6DbwU?si=LrVOrxapNjOxyojr",title:"YouTube video player",frameborder:"0",allow:"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share",referrerpolicy:"strict-origin-when-cross-origin",allowfullscreen:""})],-1),a("h2",{class:"main-heading"},"Tópico 2: Meios de Transporte; Família; Rotina e Hábitos – Em Casa",-1),a("h3",{class:"sub-heading"},"2.1 This is my family",-1),a("p",{class:"paragraph-tex"},"A conversa no pub despertou a curiosidade dos rapazes sobre a família de Rosa. Veja o diálogo:",-1),a("div",{class:"dialogue-box"},[a("iframe",{class:"responsive-video",width:"560",height:"315",src:"https://www.youtube.com/embed/sCuCGkFUmKM?si=74Cw31GlFWv_xuIY",title:"YouTube video player",frameborder:"0",allow:"accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share",referrerpolicy:"strict-origin-when-cross-origin",allowfullscreen:""}),a("p",{class:"dialogue"},[a("strong",null,"Dube:"),t(" So, Rosa, do you have any brothers or sisters?"),a("br"),a("strong",null,"Rosa:"),t(" Yes, I do. I have a sister. Her name is Patricia, she’s a student too."),a("br"),a("strong",null,"Dube:"),t(" How old is she?"),a("br"),a("strong",null,"Rosa:"),t(" She’s 19."),a("br"),a("strong",null,"Dube:"),t(" Does she look like you?"),a("br"),a("strong",null,"Rosa:"),t(" A little. She’s not very tall - she’s medium height, really. I’d say she’s slender. She has brown eyes and shoulder-length wavy dark brown hair."),a("br"),a("strong",null,"Dube:"),t(" Oh, so she’s a brunette, like you?"),a("br"),a("strong",null,"Rosa:"),t(" Yeah, but her skin is quite fair."),a("br"),a("strong",null,"Dube:"),t(" And what about your parents? What do they look like?"),a("br"),a("strong",null,"Rosa:"),t(" My father’s tall, strong, and he has brown eyes and a beard. My mother’s hair is short, curly and light brown, and she has hazel eyes. She’s short and a bit plump. They’re an interesting couple, quite a contrast!"),a("br"),a("strong",null,"Dube:"),t(" Yeah, I guess so. And the rest of your family? Do you have many relatives?"),a("br"),a("strong",null,"Billy:"),t(" Jesus Christ, Dube. You sound like you’re you changing your career. You sound like a bloody journalist. ")])],-1),d('<p class="paragraph-tex" data-v-b2213029>Rosa ao falar da família dela, menciona os graus de parentesco. No próximo tópico você estudará sobre os family members.</p><h3 class="sub-heading" data-v-b2213029>2.2 Family Members</h3><p class="paragraph-tex" data-v-b2213029>Veja na tabela a seguir como denominamos os membros da família.</p><div class="content-table" data-v-b2213029><table data-v-b2213029><tbody data-v-b2213029><tr data-v-b2213029><th data-v-b2213029>great-grandfather (bisavô)</th><th data-v-b2213029>great-grandmother (bisavó)</th><th data-v-b2213029>grandmother (avó)</th><th data-v-b2213029>grandfather (avô)</th></tr><tr data-v-b2213029><th data-v-b2213029>granddaughter (neta)</th><th data-v-b2213029>grandson (neto)</th><th data-v-b2213029>father (pai)</th><th data-v-b2213029>mother (mãe)</th></tr><tr data-v-b2213029><th data-v-b2213029>daughter (filha)</th><th data-v-b2213029>son (filho)</th><th data-v-b2213029>brother (irmão)</th><th data-v-b2213029>sister (irmã)</th></tr><tr data-v-b2213029><th data-v-b2213029>uncle (tio)</th><th data-v-b2213029>aunt (tia)</th><th data-v-b2213029>nephew (sobrinho)</th><th data-v-b2213029>niece (sobrinha)</th></tr></tbody></table></div><p class="paragraph-tex" data-v-b2213029>Além dos family members, existem outros termos relativos à família. É o caso de <strong data-v-b2213029>parents</strong>, que significa <strong data-v-b2213029>pais</strong>. <strong data-v-b2213029>Parentes</strong> em inglês é <strong data-v-b2213029>relatives</strong>, ou seja, temos aqui falsos cognatos, portanto preste atenção quando for utilizá-los. </p><p class="paragraph-tex" data-v-b2213029>Há algumas palavras que indicam uma maneira mais carinhosa e afetuosa de referir-se aos nossos <strong data-v-b2213029>relatives</strong>. Por exemplo, você pode chamar seus <strong data-v-b2213029>parents</strong> de <strong data-v-b2213029>mum</strong> e <strong data-v-b2213029>dad</strong>, ou ainda, <strong data-v-b2213029>mommy</strong> e <strong data-v-b2213029>daddy</strong>. </p><p class="paragraph-tex" data-v-b2213029>Quando você estiver conversando sobre sua family, pode ser que você precise se referir aos seus irmãos e irmãs conjuntamente. Nesse caso, se utiliza a palavra <strong data-v-b2213029>siblings</strong>. Para referir-se aos seus siblings você também pode chamá-los de <strong data-v-b2213029>sis</strong> e <strong data-v-b2213029>bro</strong>. No caso de uma meia-irmã, ou seja, uma irmã que é filha de apenas um dos pais, utiliza-se <strong data-v-b2213029>half-sister</strong>, e para um meio-irmão, <strong data-v-b2213029>half-brother</strong>. E, os seus avós poderão ser chamados de <strong data-v-b2213029>grandma</strong> e <strong data-v-b2213029>grandpa</strong>, ou quando se referir aos dois avós, de <strong data-v-b2213029>grandparents</strong>. E ainda, uma tia, pode ser chamada de uma forma mais carinhosa utilizando-se <strong data-v-b2213029>auntie</strong>. Para primos, utiliza-se <strong data-v-b2213029>cousin</strong>, tanto para homem como para mulher. </p><p class="paragraph-tex" data-v-b2213029>Se você não tiver nenhum irmão ou irmã, você pode dizer:</p><div class="content-table" data-v-b2213029><table data-v-b2213029><tbody data-v-b2213029><tr data-v-b2213029><th data-v-b2213029>I’m an only son.</th><th data-v-b2213029>Sou filho único.</th></tr><tr data-v-b2213029><th data-v-b2213029>I’m an only daughter.</th><th data-v-b2213029>Sou filha única.</th></tr><tr data-v-b2213029><th data-v-b2213029>I’m an only child.</th><th data-v-b2213029>Sou filho único.</th></tr></tbody></table></div><p class="paragraph-tex" data-v-b2213029>A palavra child significa “criança”, mas também é utilizada para se referir a filho ou filha, sem distinção de gênero. Da mesma forma, <strong data-v-b2213029>children</strong> é utilizada quando se fala em “filhos”.</p><p class="paragraph-tex" data-v-b2213029>E, falando em filhos, os pais muitas vezes escolhem uma pessoa especial para ser como um pai ou como uma mãe para a criança. Essa pessoa chama-se:</p><div class="content-table" data-v-b2213029><table data-v-b2213029><tbody data-v-b2213029><tr data-v-b2213029><th data-v-b2213029>godfather</th><th data-v-b2213029>padrinho</th></tr><tr data-v-b2213029><th data-v-b2213029>godmother</th><th data-v-b2213029>madrinha</th></tr></tbody></table></div><p class="paragraph-tex" data-v-b2213029>Existem relações de parentesco que se dão devido a um segundo ou terceiro casamento do pai ou da mãe. Veja:</p><div class="content-table" data-v-b2213029><table data-v-b2213029><tbody data-v-b2213029><tr data-v-b2213029><th data-v-b2213029>stepfather</th><th data-v-b2213029>padrasto</th></tr><tr data-v-b2213029><th data-v-b2213029>stepmother</th><th data-v-b2213029>madrasta</th></tr><tr data-v-b2213029><th data-v-b2213029>stepson</th><th data-v-b2213029>enteado</th></tr><tr data-v-b2213029><th data-v-b2213029>stepdaughter</th><th data-v-b2213029>enteada</th></tr></tbody></table></div><p class="paragraph-tex" data-v-b2213029>Também existem pessoas cujo parentesco conosco se dá em função do casamento, ou seja, não são parentes <strong data-v-b2213029>related by blood</strong> (de sangue), e sim parentes <strong data-v-b2213029>na/por meio da lei</strong> - em inglês, <strong data-v-b2213029>in-laws</strong>. Veja os nomes desses parentes em inglês:</p><div class="content-table" data-v-b2213029><table data-v-b2213029><thead data-v-b2213029><tr data-v-b2213029><th data-v-b2213029>In-law relatives</th><th data-v-b2213029>Parentes por parte do cônjuge</th></tr></thead><tbody data-v-b2213029><tr data-v-b2213029><td data-v-b2213029>mother-in-law</td><td data-v-b2213029>Sogra</td></tr><tr data-v-b2213029><td data-v-b2213029>father-in-law</td><td data-v-b2213029>Sogro</td></tr><tr data-v-b2213029><td data-v-b2213029>brother-in-law</td><td data-v-b2213029>Cunhado</td></tr><tr data-v-b2213029><td data-v-b2213029>sister-in-law</td><td data-v-b2213029>Cunhada</td></tr><tr data-v-b2213029><td data-v-b2213029>son-in-law</td><td data-v-b2213029>Genro</td></tr><tr data-v-b2213029><td data-v-b2213029>daughter-in-law</td><td data-v-b2213029>Nora</td></tr></tbody></table></div><p class="paragraph-tex" data-v-b2213029>Antes das bodas de casamento, quando o casal está noivo, <strong data-v-b2213029>fiancée</strong> para a mulher e <strong data-v-b2213029>fiancé</strong> para o homem. Com a data do casamento marcada, próximo ao dia do casamento ou no próprio dia, <strong data-v-b2213029>bride</strong> para a noiva e <strong data-v-b2213029>groom</strong> para o noivo. E antes disso, quando ainda são namorados, temos <strong data-v-b2213029>girlfriend</strong> para ela e <strong data-v-b2213029>boyfriend</strong> para ele. </p><h3 class="sub-heading" data-v-b2213029>2.3 Possessive (genitive) case</h3><p class="paragraph-tex" data-v-b2213029>No diálogo em que Rosa fala aos amigos sobre a família, ela diz:</p><ul data-v-b2213029><li data-v-b2213029>My <strong data-v-b2213029>mother’s</strong> hair is short, curly and light brown, and she has hazel eyes.</li><li data-v-b2213029>O cabelo da minha mãe é curto, cacheado e castanho claro, e ela tem olhos castanhos.)</li></ul><p class="paragraph-tex" data-v-b2213029>Nesta frase, Rosa está referindo-se ao cabelo de sua mãe. O que garante a ideia de posse é o uso de <strong data-v-b2213029>’s</strong> depois da palavra <strong data-v-b2213029>mother</strong>. O <strong data-v-b2213029>possessive case</strong>, chamado por alguns de <strong data-v-b2213029>genitive case</strong>, nada mais é do que uma indicação de posse entre a palavra que apresenta <strong data-v-b2213029>’s</strong> em seu final com relação à palavra seguinte. Em outras palavras, quem possui algo recebe o <strong data-v-b2213029>’s</strong>, e aquilo que é possuído vem logo a seguir. </p><div class="content-table" data-v-b2213029><table data-v-b2213029><thead data-v-b2213029><tr data-v-b2213029><th data-v-b2213029>English</th><th data-v-b2213029>Português</th></tr></thead><tbody data-v-b2213029><tr data-v-b2213029><td data-v-b2213029>My grandpa’s name is Lauro.</td><td data-v-b2213029>O nome do meu avô é Lauro.</td></tr><tr data-v-b2213029><td data-v-b2213029>My grandpas’ names are Lauro and João.</td><td data-v-b2213029>O nome dos meus avôs são Lauro e João.</td></tr></tbody></table></div><p class="paragraph-tex" data-v-b2213029>Observe que a primeira oração está no singular (é um avô). Já a segunda oração, que está no plural, indica que os nomes dos avôs são Lauro e João. Acrescenta-se <strong data-v-b2213029>’s</strong> à palavra <strong data-v-b2213029>grandpa</strong> quando estiver no singular, para demonstrar a posse daquilo que vem a seguir. No entanto quando a palavra estiver no plural - <strong data-v-b2213029>grandpas’</strong> - acrescenta-se apenas o apóstrofo, não se duplicando o “s”. </p><p class="paragraph-tex" data-v-b2213029>Recorde que na formação dos plurais das palavras em inglês, existem os plurais irregulares. É o caso de, por exemplo, person que no plural fica people. Ou ainda, woman, que no plural fica women. Como estas palavras não recebem o “s” do plural, diz-se que elas são um plural irregular. Neste caso, o acréscimo do ’s funciona normalmente. Veja os exemplos:</p><ul data-v-b2213029><li data-v-b2213029>The <strong data-v-b2213029>children’s</strong> names are Patricia and Luis. (Os nomes das crianças são Patricia e Luis) </li><li data-v-b2213029>The <strong data-v-b2213029>women’s</strong> power in society is increasing. (O poder da mulher na sociedade está aumentando)</li></ul><p class="paragraph-tex" data-v-b2213029>Na tabela a seguir você pode visualizar três utilizações do Possessive case:</p><div class="content-table" data-v-b2213029><table data-v-b2213029><thead data-v-b2213029><tr data-v-b2213029><th data-v-b2213029></th><th data-v-b2213029>English</th><th data-v-b2213029>Português</th></tr></thead><tbody data-v-b2213029><tr data-v-b2213029><td data-v-b2213029>uncle = singular</td><td data-v-b2213029>My uncle’s farm is a great place</td><td data-v-b2213029>A fazenda do meu tio é um lugar ótimo</td></tr><tr data-v-b2213029><td data-v-b2213029>people = plural</td><td data-v-b2213029>Carlton sometimes gets lost with people&#39;s orders</td><td data-v-b2213029>às vezes o Carlton se perde com os pedidos das pessoas</td></tr><tr data-v-b2213029><td data-v-b2213029>brothers = plural</td><td data-v-b2213029>My brother&#39;s names are John and Paul</td><td data-v-b2213029>O nome dos meus irmãos é John e Paul</td></tr></tbody></table></div><p class="paragraph-tex" data-v-b2213029>Já quando se tratar de um nome próprio que termine em “s”, como, por exemplo, Marcos, vale a regra: acrescenta-se o ’s. Nestes casos admite-se o uso de dois “s”. Veja:</p><div class="content-table" data-v-b2213029><table data-v-b2213029><thead data-v-b2213029><tr data-v-b2213029><th data-v-b2213029>English</th><th data-v-b2213029>Português</th></tr></thead><tbody data-v-b2213029><tr data-v-b2213029><td data-v-b2213029>Marcos’s grandma isn’t very old.</td><td data-v-b2213029>A vó de Marcos não é muito velha.</td></tr><tr data-v-b2213029><td data-v-b2213029>Chris’s family is big.</td><td data-v-b2213029>A família de Chris é grande.</td></tr></tbody></table></div><p class="paragraph-tex" data-v-b2213029>Por último, quando você precisar mencionar vários donos, terá que analisar a situação. Observe:</p><div class="content-table" data-v-b2213029><table data-v-b2213029><thead data-v-b2213029><tr data-v-b2213029><th data-v-b2213029>English</th><th data-v-b2213029>Português</th></tr></thead><tbody data-v-b2213029><tr data-v-b2213029><td data-v-b2213029>Rosa, Christine, Amanda and Carly&#39;s house is big.</td><td data-v-b2213029>A casa de Rosa, Christine, Amanda e Carly é grande</td></tr></tbody></table></div><p class="paragraph-tex" data-v-b2213029>Quando a posse é comum a todos os mencionados, o <strong data-v-b2213029>’s</strong> aparece apenas junto ao nome do último dos donos. Neste exemplo a casa pertence às quatro meninas.</p><div class="content-table" data-v-b2213029><table data-v-b2213029><thead data-v-b2213029><tr data-v-b2213029><th data-v-b2213029>English</th><th data-v-b2213029>Português</th></tr></thead><tbody data-v-b2213029><tr data-v-b2213029><td data-v-b2213029>Rosa’s, Christine’s, Amanda’s and Carly’s houses are big.</td><td data-v-b2213029>As casas de Rosa, Christine, Amanda e Rosa são grandes.</td></tr></tbody></table></div><p class="paragraph-tex" data-v-b2213029>Neste exemplo cada uma possui sua casa, por isso, cada nome recebe o ’s.</p><h3 class="sub-heading" data-v-b2213029>2.4 Question word: WHOSE</h3><p class="paragraph-tex" data-v-b2213029>Para perguntar de quem alguma coisa é ou a quem alguma coisa pertence a question word utilizada é whose. A pergunta segue a seguinte estrutura:</p><div class="content-table" data-v-b2213029><table data-v-b2213029><thead data-v-b2213029><tr data-v-b2213029><th data-v-b2213029>question word</th><th data-v-b2213029>o que é possuído</th><th data-v-b2213029>verb to be</th><th data-v-b2213029>complemento</th></tr></thead><tbody data-v-b2213029><tr data-v-b2213029><td data-v-b2213029>Whose</td><td data-v-b2213029>bicycles</td><td data-v-b2213029>are</td><td data-v-b2213029>those?</td></tr></tbody></table></div><p class="paragraph-tex" data-v-b2213029>Como você pode ver, whose precede a coisa possuída (bicycles) e é seguido de are (ou is) e de um demonstrative (this, these, that, those), dependendo do objeto possuído estar no singular ou plural, perto ou longe, de quem pergunta sobre a posse. Veja outros exemplos:</p><div class="content-table" data-v-b2213029><table data-v-b2213029><thead data-v-b2213029><tr data-v-b2213029><th data-v-b2213029>English</th><th data-v-b2213029>Português</th></tr></thead><tbody data-v-b2213029><tr data-v-b2213029><td data-v-b2213029>Whose house is this?</td><td data-v-b2213029>De quem é esta casa?</td></tr><tr data-v-b2213029><td data-v-b2213029>Whose car is that?</td><td data-v-b2213029>De quem é aquele carro?</td></tr><tr data-v-b2213029><td data-v-b2213029>Whose books are these?</td><td data-v-b2213029>De quem são estes livros?</td></tr></tbody></table></div><p class="paragraph-tex" data-v-b2213029>As perguntas com <strong data-v-b2213029>whose</strong> não precisam se limitar apenas a questões envolvendo objetos. Elas podem ser utilizadas quando desejamos obter informações sobre pessoas. Por exemplo: </p><div class="content-table" data-v-b2213029><table data-v-b2213029><thead data-v-b2213029><tr data-v-b2213029><th data-v-b2213029>English</th><th data-v-b2213029>Português</th></tr></thead><tbody data-v-b2213029><tr data-v-b2213029><td data-v-b2213029>Whose child is this?</td><td data-v-b2213029>De quem é esta criança?</td></tr><tr data-v-b2213029><td data-v-b2213029>Whose children are those?</td><td data-v-b2213029>De quem são aquelas crianças?</td></tr></tbody></table></div><p class="paragraph-tex" data-v-b2213029>Finalmente, para responder perguntas com whose sobre a posse de algo, é muito simples: basta utilizar o possessive case. Observe:</p><img src="'+i+'" alt="" class="responsive-image" data-v-b2213029><div class="content-table" data-v-b2213029><table data-v-b2213029><tbody data-v-b2213029><tr data-v-b2213029><td data-v-b2213029><strong data-v-b2213029>Whose</strong> cellphone is this?</td><td data-v-b2213029>It’s <strong data-v-b2213029>Christine’s</strong></td></tr><tr data-v-b2213029><td data-v-b2213029><strong data-v-b2213029>Whose</strong> bicycle is that?</td><td data-v-b2213029>It’s <strong data-v-b2213029>Dube’s</strong>.</td></tr></tbody></table></div><img src="'+b+'" alt="" class="responsive-image" data-v-b2213029><div class="content-table" data-v-b2213029><table data-v-b2213029><tbody data-v-b2213029><tr data-v-b2213029><td data-v-b2213029><strong data-v-b2213029>Whose</strong> books are these?</td><td data-v-b2213029>They’re <strong data-v-b2213029>Rosa’s</strong></td></tr><tr data-v-b2213029><td data-v-b2213029><strong data-v-b2213029>Whose</strong> bags are those?</td><td data-v-b2213029>They’re <strong data-v-b2213029>Amanda’s</strong> and <strong data-v-b2213029>Carly’s</strong>.</td></tr></tbody></table></div><h3 class="sub-heading" data-v-b2213029>2.5 Present Simple - verb To have</h3><p class="paragraph-tex" data-v-b2213029>Para falar sobre sua família, Rosa utiliza o verbo To have (ter) no Present Simple, já que ela têm uma irmã, tem uma pai e uma mãe, enfim, isso faz parte do presente, da rotina dela.</p><p class="paragraph-tex" data-v-b2213029>O Present Simple em inglês é parecido com o presente do indicativo no português e serve para expressar:</p><h3 class="sub-heading" data-v-b2213029>Fatos</h3><p class="paragraph-tex" data-v-b2213029>Humans have two legs.</p><h3 class="sub-heading" data-v-b2213029>Verdades universais</h3><p class="paragraph-tex" data-v-b2213029>The moon is a satellite.</p><h3 class="sub-heading" data-v-b2213029>Rotina ou atividades habituais</h3><p class="paragraph-tex" data-v-b2213029>I have classes every day.</p><p class="paragraph-tex" data-v-b2213029>Uma das características mais evidentes do Present Simple é o fato de que, em frases afirmativas, os verbos não são alterados, quer dizer, você os utiliza em sua forma original - eles não são “conjugados”. Mas há uma exceção: a 3ª pessoa do singular, ou seja, he, she e it.</p><p class="paragraph-tex" data-v-b2213029>Os verbos, quando utilizados com esses pronomes (he, she e it) ou com os nomes das pessoas ou coisas que eles representam, recebem o acréscimo de “s”. Mas no caso do verbo To have há uma irregularidade. Observe a conjugação:</p><div class="content-table" data-v-b2213029><table data-v-b2213029><thead data-v-b2213029><tr data-v-b2213029><th colspan="3" data-v-b2213029>VERB TO HAVE</th></tr></thead><tbody data-v-b2213029><tr data-v-b2213029><td data-v-b2213029>I</td><td rowspan="2" data-v-b2213029>Have</td><td rowspan="7" data-v-b2213029>A sister</td></tr><tr data-v-b2213029><td data-v-b2213029>You</td></tr><tr data-v-b2213029><td data-v-b2213029>He</td><td rowspan="2" data-v-b2213029>Has</td></tr><tr data-v-b2213029><td data-v-b2213029>She</td></tr><tr data-v-b2213029><td data-v-b2213029>We</td><td rowspan="3" data-v-b2213029>Have</td></tr><tr data-v-b2213029><td data-v-b2213029>You</td></tr><tr data-v-b2213029><td data-v-b2213029>They</td></tr></tbody></table></div><p class="paragraph-tex" data-v-b2213029>Observe que na tabela it não aparece. Isso é porque não faria sentido dizer que it has a sister, já que &quot;it&quot; refere-se a objetos ou coisas. Mas se estivéssemos nos referindo a, por exemplo, uma doceria, poderíamos dizer: It has the best pies in town! &quot;Ela (a doceria) tem as melhores tortas da cidade&quot;. </p><p class="paragraph-tex" data-v-b2213029>Ao falar que tem uma irmã, Rosa diz:</p><ul data-v-b2213029><li data-v-b2213029>I <strong data-v-b2213029>have</strong> a sister.</li></ul><p class="paragraph-tex" data-v-b2213029>Mas, ao falar que sua irmã tem olhos castanhos, ela diz:</p><ul data-v-b2213029><li data-v-b2213029>She <strong data-v-b2213029>has</strong> brown eyes.</li></ul><p class="paragraph-tex" data-v-b2213029>A segunda característica do Present Simple é que, como os outros tempos verbais em inglês, ele possui um <strong data-v-b2213029>verbo auxiliar</strong>. Os verbos auxiliares em inglês não possuem em si um significado, quer dizer, não podem ser traduzidos, mas têm duas funções:</p><ol data-v-b2213029><li data-v-b2213029>1) indicar qual é o tempo verbal da oração;</li><li data-v-b2213029>2) compor suas formas interrogativa e negativa.</li></ol><p class="paragraph-tex" data-v-b2213029>Em relação à forma interrogativa, no Present Simple, utiliza-se do antes do sujeito na composição de perguntas, porém, assim como ocorre na afirmativa, há uma diferença quando se trata da 3ª pessoa do singular: ao invés de do usamos does. Compare:</p><div class="content-table" data-v-b2213029><table data-v-b2213029><thead data-v-b2213029><tr data-v-b2213029><th colspan="3" data-v-b2213029>VERB TO HAVE</th></tr></thead><tbody data-v-b2213029><tr data-v-b2213029><td rowspan="2" data-v-b2213029>Do</td><td data-v-b2213029>I</td><td rowspan="7" data-v-b2213029>have a sister?</td></tr><tr data-v-b2213029><td data-v-b2213029>You</td></tr><tr data-v-b2213029><td rowspan="2" data-v-b2213029>Does</td><td data-v-b2213029>He</td></tr><tr data-v-b2213029><td data-v-b2213029>She</td></tr><tr data-v-b2213029><td rowspan="3" data-v-b2213029>Do</td><td data-v-b2213029>We</td></tr><tr data-v-b2213029><td data-v-b2213029>You</td></tr><tr data-v-b2213029><td data-v-b2213029>They</td></tr></tbody></table></div><p class="paragraph-tex" data-v-b2213029>Dube pergunta a Rosa:</p><ul data-v-b2213029><li data-v-b2213029><strong data-v-b2213029>Do you</strong> have any brothers or sisters?</li></ul><p class="paragraph-tex" data-v-b2213029>Mas, se Dube estivesse falando com alguém sobre a Rosa, e perguntasse a seu respeito, a pergunta ficaria:</p><ul data-v-b2213029><li data-v-b2213029><strong data-v-b2213029>Does she</strong> have any brothers or sisters?</li></ul><p class="paragraph-tex" data-v-b2213029>Como você pode perceber, o <strong data-v-b2213029>“s”</strong> que marca a 3ª pessoa se desloca para o auxiliar <strong data-v-b2213029>does</strong>, enquanto o verbo principal não sofre modificação alguma, ou seja, permanece <strong data-v-b2213029>have</strong> para todas as pessoas. </p><p class="paragraph-tex" data-v-b2213029>Já para a formação de negativas no Present Simple, é necessário mais do que simplesmente acrescentar a partícula negativa not junto ao verbo. O auxiliar deve estar presente cumprindo a dupla função de indicar qual é o tempo verbal da oração e de compor a forma negativa.</p><p class="paragraph-tex" data-v-b2213029>O auxiliar é utilizado entre o sujeito e o verbo principal, além de ser acompanhado de <strong data-v-b2213029>not</strong>. Veja: </p><div class="content-table" data-v-b2213029><table data-v-b2213029><thead data-v-b2213029><tr data-v-b2213029><th colspan="3" data-v-b2213029>VERB TO HAVE</th></tr></thead><tbody data-v-b2213029><tr data-v-b2213029><td data-v-b2213029>I</td><td rowspan="2" data-v-b2213029>do not / don&#39;t</td><td rowspan="7" data-v-b2213029>have a sister</td></tr><tr data-v-b2213029><td data-v-b2213029>You</td></tr><tr data-v-b2213029><td data-v-b2213029>He</td><td rowspan="2" data-v-b2213029>does not / doesn&#39;t</td></tr><tr data-v-b2213029><td data-v-b2213029>She</td></tr><tr data-v-b2213029><td data-v-b2213029>We</td><td rowspan="3" data-v-b2213029>do not / don&#39;t</td></tr><tr data-v-b2213029><td data-v-b2213029>You</td></tr><tr data-v-b2213029><td data-v-b2213029>They</td></tr></tbody></table></div><ul data-v-b2213029><li data-v-b2213029>I <strong data-v-b2213029>do not</strong> have a sister. I <strong data-v-b2213029>don’t</strong> have a sister.</li><li data-v-b2213029>He <strong data-v-b2213029>does not</strong> have a sister. He <strong data-v-b2213029>doesn’t</strong> have a sister.</li></ul><p class="paragraph-tex" data-v-b2213029>Note que <strong data-v-b2213029>don’t</strong> e <strong data-v-b2213029>doesn’t</strong> são simplesmente a contraction do auxiliar com a partícula negativa (do+not = don’t; does+not = doesn’t) e são mais utilizados do que a forma por extenso, escolhida na escrita formal ou quando se quer dar ênfase ao <strong data-v-b2213029>not</strong>. </p><p class="paragraph-tex" data-v-b2213029>Don’t e doesn’t também são utilizados nas short answers do Present Simple. Acompanhe: </p><div class="content-table" data-v-b2213029><table data-v-b2213029><tbody data-v-b2213029><tr data-v-b2213029><td data-v-b2213029><strong data-v-b2213029>Does Rosa</strong> have a sister?</td><td data-v-b2213029>Yes, <strong data-v-b2213029>she does</strong></td></tr><tr data-v-b2213029><td data-v-b2213029><strong data-v-b2213029>Does Rosa</strong> have a brother?</td><td data-v-b2213029>No, <strong data-v-b2213029>she doesn’t</strong>.</td></tr><tr data-v-b2213029><td data-v-b2213029><strong data-v-b2213029>Does Billy</strong> have an uncle?</td><td data-v-b2213029>Yes, <strong data-v-b2213029>he does</strong>.</td></tr><tr data-v-b2213029><td data-v-b2213029><strong data-v-b2213029>Do Christine and Dube</strong> love each other?</td><td data-v-b2213029>Yes, <strong data-v-b2213029>they do</strong>.</td></tr></tbody></table></div><p class="paragraph-tex" data-v-b2213029>Como você pode ver, o mesmo auxiliar que é utilizado nas questions é o que se usa nas short answers. Além disso, é importante lembrar que, exceto em contextos informais, é sempre melhor utilizar as short answers ao invés de responder simplesmente yes ou no.</p><p class="paragraph-tex" data-v-b2213029>O Present Simple e o verbo To have são muito usados no dia a dia. Este verbo também é usado no momento em que vamos descrever alguém. Acompanhe este conteúdo no tópico a seguir.</p><h3 class="sub-heading" data-v-b2213029>2.6 Describing people</h3><p class="paragraph-tex" data-v-b2213029>Depois de muito falarem sobre a família de Rosa, a garota pergunta sobre a família de Victor. Acompanhe a conversa:</p><img src="'+l+'" alt="" class="responsive-image" data-v-b2213029><div class="dialogue-box" data-v-b2213029><p class="dialogue" data-v-b2213029><strong data-v-b2213029>Rosa:</strong> So, guys! Enough talking about me and my family. What about you? Do you have brothers and sisters?<br data-v-b2213029><strong data-v-b2213029>Victor:</strong> Yes, I do. I have one brother and one sister. Their names are Peter and Rebeca.<br data-v-b2213029><strong data-v-b2213029>Billy:</strong> Hummm… how old is she?<br data-v-b2213029><strong data-v-b2213029>Victor:</strong> She’s 25! Why?<br data-v-b2213029><strong data-v-b2213029>Billy:</strong> Hmmm… How does she look like?<br data-v-b2213029><strong data-v-b2213029>Victor:</strong> Well… She’s slender. I guess… she has long wavy hair. She has beautiful green eyes.<br data-v-b2213029><strong data-v-b2213029>Billy:</strong> Hmmm… interesting.<br data-v-b2213029><strong data-v-b2213029>Rosa:</strong> Victor, is she blonde, like you?<br data-v-b2213029><strong data-v-b2213029>Victor:</strong> Not really, her hair is a little red.<br data-v-b2213029><strong data-v-b2213029>Billy:</strong> Hmmm… interesting.<br data-v-b2213029><strong data-v-b2213029>Rosa:</strong> And, is she tall?<br data-v-b2213029><strong data-v-b2213029>Victor:</strong> Well, she’s medium-height.<br data-v-b2213029><strong data-v-b2213029>Billy:</strong> Hmmm… interesting.<br data-v-b2213029><strong data-v-b2213029>Victor:</strong> What’s your problem, man?<br data-v-b2213029><strong data-v-b2213029>Billy:</strong> Calm down, dude! </p></div><p data-v-b2213029>Quando Victor descreveu a aparência física de sua irmã para Rosa e Billy, ele utilizou dois verbos diferentes: o verbo To be e o verbo To have, já que precisava dizer como ela <strong data-v-b2213029>é</strong> fisicamente e que outras características corporais ela <strong data-v-b2213029>tem</strong>. Retome algumas das falas de Victor:</p><div class="dialogue-box" data-v-b2213029><p class="dialogue" data-v-b2213029><strong data-v-b2213029>Victor:</strong> She’s slender. I guess… she has long wavy hair. She has beautiful green eyes.<br data-v-b2213029><strong data-v-b2213029>Victor:</strong> Her hair is a little red.<br data-v-b2213029><strong data-v-b2213029>Victor:</strong> She’s medium-height. </p></div><p class="paragraph-tex" data-v-b2213029>Victor utilizou alguns adjetivos para descrever sua irmã Rebeca. Eles são relacionados à tipo e cor de cabelo, olhos e altura. Acompanhe a seguir outros adjetivos:</p><h3 class="sub-heading" data-v-b2213029>Height (Altura):</h3><div class="content-img" data-v-b2213029><img src="'+v+'" alt="" class="styled-img flags" data-v-b2213029><img src="'+g+'" alt="" class="styled-img flags" data-v-b2213029><img src="'+p+'" alt="" class="styled-img flags" data-v-b2213029></div><h3 class="sub-heading" data-v-b2213029>Body Type (Tipo Físico):</h3><div class="content-img" data-v-b2213029><img src="'+u+'" alt="" class="styled-img flags" data-v-b2213029><img src="'+m+'" alt="" class="styled-img flags" data-v-b2213029><img src="'+h+'" alt="" class="styled-img flags" data-v-b2213029><img src="'+c+'" alt="" class="styled-img flags" data-v-b2213029><img src="'+A+'" alt="" class="styled-img flags" data-v-b2213029><img src="'+f+'" alt="" class="styled-img flags" data-v-b2213029><img src="'+y+'" alt="" class="styled-img flags" data-v-b2213029><img src="'+C+'" alt="" class="styled-img flags" data-v-b2213029></div><h3 class="sub-heading" data-v-b2213029>Hair Length (Comprimento do cabelo):</h3><div class="content-img" data-v-b2213029><img src="'+w+'" alt="" class="styled-img flags" data-v-b2213029><img src="'+D+'" alt="" class="styled-img flags" data-v-b2213029><img src="'+B+'" alt="" class="styled-img flags" data-v-b2213029></div><h3 class="sub-heading" data-v-b2213029>Hair Shape (Tipo de Cabelo):</h3><div class="content-img" data-v-b2213029><img src="'+I+'" alt="" class="styled-img flags" data-v-b2213029><img src="'+Q+'" alt="" class="styled-img flags" data-v-b2213029><img src="'+E+'" alt="" class="styled-img flags" data-v-b2213029></div><h3 class="sub-heading" data-v-b2213029>Hair Color (Cor do Cabelo):</h3><div class="content-img" data-v-b2213029><img src="'+x+'" alt="" class="styled-img flags" data-v-b2213029><img src="'+H+'" alt="" class="styled-img flags" data-v-b2213029><img src="'+q+'" alt="" class="styled-img flags" data-v-b2213029><img src="'+R+'" alt="" class="styled-img flags" data-v-b2213029><img src="'+P+'" alt="" class="styled-img flags" data-v-b2213029><img src="'+Y+'" alt="" class="styled-img flags" data-v-b2213029></div><p class="paragraph-tex" data-v-b2213029>Diz-se que o cabelo é red, mas se queremos dizer que uma pessoa é ruiva, a palavra usada é redhead: He’s a redhead. Já para alguém que tem cabelos escuros, diz-se dark ou black. Mas, se queremos dizer que a pessoa é morena, usa-se brunette: She’s a brunette. Brunette é mais comumente associado a mulheres. Já blond designa a cor de um cabelo. Mas, se queremos dizer que uma pessoa é loiro ou loira, usamos, respectivamente, blond e blonde.</p><h3 class="sub-heading" data-v-b2213029>Eyes (Olhos):</h3><div class="content-img" data-v-b2213029><img src="'+G+'" alt="" class="styled-img flags" data-v-b2213029><img src="'+S+'" alt="" class="styled-img flags" data-v-b2213029><img src="'+z+'" alt="" class="styled-img flags" data-v-b2213029><img src="'+O+'" alt="" class="styled-img flags" data-v-b2213029></div><h3 class="sub-heading" data-v-b2213029>Other Features (Outras características):</h3><div class="content-img" data-v-b2213029><img src="'+M+'" alt="" class="styled-img flags" data-v-b2213029><img src="'+Z+'" alt="" class="styled-img flags" data-v-b2213029><img src="'+F+'" alt="" class="styled-img flags" data-v-b2213029></div><h3 class="sub-heading" data-v-b2213029>Age (Idade)</h3><div class="content-img" data-v-b2213029><img src="'+V+'" alt="" class="styled-img flags" data-v-b2213029><img src="'+U+'" alt="" class="styled-img flags" data-v-b2213029><img src="'+j+'" alt="" class="styled-img flags" data-v-b2213029></div><p class="paragraph-tex" data-v-b2213029>Utilizando o verbo To be e o To have associado a este vocabulário é possível que você já consiga descrever pessoas. No entanto, é necessário estudar a ordem dos adjetivos para que a comunicação seja feita de maneira adequada.</p><h3 class="sub-heading" data-v-b2213029>2.7 Order of adjectives</h3><p class="paragraph-tex" data-v-b2213029>Você já viu que os adjetivos em inglês têm que estar colocados em uma posição específica quando estão junto ao substantivo: logo à sua frente. Ao usar mais de um adjetivo para caracterizar um mesmo elemento, não se deve usar vírgula entre eles. É por essa razão que, ao descrever sua irmã Patrícia, Rosa disse:</p><ul data-v-b2213029><li style="text-align:center;" data-v-b2213029><strong data-v-b2213029>She has... shoulder-length wavy dark brown hair.</strong></li></ul><p class="paragraph-tex" data-v-b2213029>Ao se utilizar mais de um adjetivo para algo ou alguém, devemos obedecer uma ordem. Veja:</p><div class="content-table" data-v-b2213029><table data-v-b2213029><thead data-v-b2213029><tr data-v-b2213029><th data-v-b2213029>ORDER OF ADJECTIVES</th><th data-v-b2213029>ORDEM DOS ADJETIVOS</th></tr></thead><tbody data-v-b2213029><tr data-v-b2213029><td data-v-b2213029>Opinion</td><td data-v-b2213029>Opinião (bonito, interessante, etc.)</td></tr><tr data-v-b2213029><td data-v-b2213029>Size</td><td data-v-b2213029>Tamanho (altura, comprimento, etc.)</td></tr><tr data-v-b2213029><td data-v-b2213029>Age</td><td data-v-b2213029>Idade</td></tr><tr data-v-b2213029><td data-v-b2213029>Shape</td><td data-v-b2213029>Formato</td></tr><tr data-v-b2213029><td data-v-b2213029>Color</td><td data-v-b2213029>Cor</td></tr><tr data-v-b2213029><td data-v-b2213029>Origin</td><td data-v-b2213029>Origem</td></tr><tr data-v-b2213029><td data-v-b2213029>Material</td><td data-v-b2213029>Material</td></tr></tbody></table></div><p class="paragraph-tex" data-v-b2213029>Seguindo essa ordem, você pode descrever alguém como:</p><div class="content-table" data-v-b2213029><table data-v-b2213029><tbody data-v-b2213029><tr data-v-b2213029><td data-v-b2213029><strong data-v-b2213029>Carly is an</strong> enchanting tall young blonde woman.</td><td data-v-b2213029>Carly é uma mulher alta, jovem loira e encantadora.</td></tr><tr data-v-b2213029><td data-v-b2213029><strong data-v-b2213029>Billy is a</strong> medium-height skinny brown hair guy.</td><td data-v-b2213029>Billy é um rapaz de estatura mediana, cabelo castanho e magricelo.</td></tr></tbody></table></div><p class="paragraph-tex" data-v-b2213029>Observe que antes dos adjetivos vem o numeral ou artigo, que pode ser a, an, the, two, a dozen, etc. Veja:</p><div class="content-table" data-v-b2213029><table data-v-b2213029><tbody data-v-b2213029><tr data-v-b2213029><td data-v-b2213029><strong data-v-b2213029>A</strong> fantastic small new red Japanese car.</td><td data-v-b2213029>Um fantástico carro japonês pequeno e vermelho</td></tr><tr data-v-b2213029><td data-v-b2213029><strong data-v-b2213029>Two</strong> beautiful old round Indian mahogany tables.</td><td data-v-b2213029>Duas lindas mesas antigas, redondas, indianas e de mogno.</td></tr></tbody></table></div><p class="paragraph-tex" data-v-b2213029>Um último detalhe importante quanto ao uso de adjetivos em inglês é que ele não concorda nem em número, nem em gênero com o substantivo que o acompanha. Perceba isso nas frases a seguir: </p><div class="content-table" data-v-b2213029><table data-v-b2213029><thead data-v-b2213029><tr data-v-b2213029><th data-v-b2213029>Singular female</th><th data-v-b2213029>Plural female</th><th data-v-b2213029>Singular male</th><th data-v-b2213029>Plural male</th></tr></thead><tbody data-v-b2213029><tr data-v-b2213029><td data-v-b2213029>tall girl</td><td data-v-b2213029>tall girls</td><td data-v-b2213029>tall boy</td><td data-v-b2213029>tall boys</td></tr></tbody></table></div><h3 class="sub-heading" data-v-b2213029>2.8 Material Complementar - &#39;S: &quot;possessive&quot; or &quot;is&quot;</h3><p class="paragraph-tex" data-v-b2213029>É comum acontecer de as pessoas ficarem na dúvida se um &#39;s representa o caso possessivo ou a forma da 3ª pessoa do singular do verbo To be (is) formando uma contraction com seu sujeito. Como saber a diferença, então? A maneira de verificar de qual dos dois se trata é pelo sentido da frase. Veja os exemplos:</p><p class="paragraph-tex" data-v-b2213029><strong data-v-b2213029>She&#39;s my friend. John’s sister is beautiful.</strong> No primeiro exemplo, a palavra my não pode ser possuída pelo pronome she, pois não faria o menor sentido (O dela meu amigo), não é verdade? Já no segundo exemplo, a frase ganha sentido se o &#39;s representar o possessive case (do contrário teríamos John é irmã é bonita). Por isso, é preciso um pouco de atenção para não confundir a contração de is com o possessive case. Verifique se a palavra com &#39;s é o possuidor da palavra seguinte, a qual, por sua vez é o objeto possuído. Se não for, então você está diante do verbo To be.</p><h3 class="sub-heading" data-v-b2213029>2.9 Material Complementar - getting on - Have vs. Have got</h3><p class="paragraph-tex" data-v-b2213029>Have e have got têm o mesmo significado e em geral pode ser utilizada uma no lugar da outra quando se fala de posse, relações de parentesco, doenças e características físicas de pessoas ou coisas no <strong data-v-b2213029>Present Simple</strong>. A diferença entre eles é que have got é mais informal, e é mais utilizado no inglês britânico (BrE).</p><p class="paragraph-tex" data-v-b2213029>Entretanto, existem diferenças importantes em relação à forma e ao uso de have e have got, as quais devem ser levadas em consideração se você for utilizar have got:</p><ul data-v-b2213029><li data-v-b2213029><strong data-v-b2213029>Have got</strong> é utilizado apenas no <strong data-v-b2213029>Present Simple</strong>. Em aulas futuras você verá outros tempos verbais, mas have got não poderá ser utilizado neles.</li><li data-v-b2213029><strong data-v-b2213029>Have got</strong> é composto de duas partes, uma delas é o próprio auxiliar – o <strong data-v-b2213029>have</strong>. O verbo To Have, chamado aqui apenas have, é um verbo simples, portanto quando utilizado nas formas interrogativa e negativa do Present Simple ele necessita dos auxiliares do ou does. Como have got possui duas partes (have + got), uma delas – no caso, have – pode cumprir essa função. </li></ul><h3 class="sub-heading" data-v-b2213029>2.10 Resumo e Glossário</h3><p class="paragraph-tex" data-v-b2213029>Glossary</p><div class="glossary" data-v-b2213029><span data-v-b2213029>Medium-height/average-height:</span> altura média; <span data-v-b2213029>Shoulder-length:</span> na altura do ombro; <span data-v-b2213029>Middle-aged:</span> de meia idade e; <span data-v-b2213029>Other features:</span> outras características; <span data-v-b2213029>Hazel:</span> avelã/castanho esverdeados; <span data-v-b2213029>Fat/overweight:</span> gordo/ acima do peso; <span data-v-b2213029>Legs:</span> pernas; <span data-v-b2213029>Moon:</span> lua; <span data-v-b2213029>Enough:</span> suficiente; <span data-v-b2213029>To look like:</span> parecer(-se) com; <span data-v-b2213029>Slim/Slender:</span> esbelto; <span data-v-b2213029>Wavy:</span> ondulado; <span data-v-b2213029>Eyes:</span> olhos; <span data-v-b2213029>Blonde:</span> loira; <span data-v-b2213029>Hair:</span> cabelo; <span data-v-b2213029>Tall:</span> alto; <span data-v-b2213029>Medium-high:</span> altura mediana; <span data-v-b2213029>Calm down:</span> calma; <span data-v-b2213029>Height:</span> altura; <span data-v-b2213029>Short:</span> baixo/curto; <span data-v-b2213029>Plump:</span> cheinho; <span data-v-b2213029>Thin:</span> magro; <span data-v-b2213029>Skinny:</span> muito magro; <span data-v-b2213029>Stout:</span> troncudo; <span data-v-b2213029>Strong:</span> forte; <span data-v-b2213029>Weak:</span> fraco; <span data-v-b2213029>Length:</span> comprimento; <span data-v-b2213029>Long:</span> comprido; <span data-v-b2213029>Shape:</span> formato/tipo; <span data-v-b2213029>Straight:</span> liso; <span data-v-b2213029>Curly:</span> crespo; <span data-v-b2213029>Dark/black:</span> escuro/preto; <span data-v-b2213029>Brown:</span> castanho; <span data-v-b2213029>Blond:</span> loiro; <span data-v-b2213029>Gray:</span> grisalho; <span data-v-b2213029>Red:</span> ruivo; <span data-v-b2213029>Dyed:</span> tingido; <span data-v-b2213029>Green:</span> verdes; <span data-v-b2213029>Blue:</span> azuis; <span data-v-b2213029>Mustache:</span> bigode; <span data-v-b2213029>Beard:</span> barba; <span data-v-b2213029>Goatee:</span> cavanhaque; <span data-v-b2213029>Bald:</span> careca; <span data-v-b2213029>Age:</span> idade; <span data-v-b2213029>Young:</span> jovem; <span data-v-b2213029>Old:</span> idoso. </div>',127)])))}}),T=n(W,[["__scopeId","data-v-b2213029"]]);export{T as default};
